import React, { useState, useEffect } from 'react';
import { 
  Box, Typography, Grid, Paper, Card, CardContent, 
  CardHeader, Divider, List, ListItem, ListItemText, 
  ListItemAvatar, Avatar, CircularProgress, Button,
  Tabs, Tab, Alert, Container, Chip
} from '@mui/material';
import TrendingUpIcon from '@mui/icons-material/TrendingUp';
import TrendingDownIcon from '@mui/icons-material/TrendingDown';
import ShowChartIcon from '@mui/icons-material/ShowChart';
import RefreshIcon from '@mui/icons-material/Refresh';
import { useNavigate, useLocation } from 'react-router-dom';
import axios from 'axios';
import { 
  LineChart, Line, BarChart, Bar, XAxis, YAxis, CartesianGrid, 
  Tooltip, Legend, ResponsiveContainer 
} from 'recharts';
import ArrowUpwardIcon from '@mui/icons-material/ArrowUpward';
import ArrowDownwardIcon from '@mui/icons-material/ArrowDownward';
import { Link } from 'react-router-dom';
import StockCrossingAlert from '../components/StockCrossingAlert';

const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:8000';

const Dashboard = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const [loading, setLoading] = useState(true);
  const [marketData, setMarketData] = useState(null);
  const [topGainers, setTopGainers] = useState([]);
  const [topLosers, setTopLosers] = useState([]);
  const [mostActive, setMostActive] = useState([]);
  const [popularStocks, setPopularStocks] = useState([]);
  const [crossingStocks, setCrossingStocks] = useState([]);
  const [recentlyCrossedStocks, setRecentlyCrossedStocks] = useState([]);
  const [error, setError] = useState('');
  const [activeTab, setActiveTab] = useState('gainers');
  const [lastUpdated, setLastUpdated] = useState(new Date());
  const [usingDemoData, setUsingDemoData] = useState(false);

  // Parse the filter from URL query parameters
  const urlParams = new URLSearchParams(location.search);
  const filter = urlParams.get('filter');

  useEffect(() => {
    const params = new URLSearchParams(location.search);
    const filterParam = params.get('filter');

    if (filterParam) {
      if (['popular', 'gainers', 'losers', 'most-active'].includes(filterParam)) {
        setActiveTab(filterParam);
      }
    } else {
      // Set the default tab to 'gainers' when no filter parameter is provided
      setActiveTab('gainers');
      // Update URL to reflect the default tab
      navigate('?filter=gainers', { replace: true });
    }
  }, [location.search, navigate]);

  useEffect(() => {
    console.log("Dashboard component mounted");

    // Add this to help debug API URL issues
    console.log("API URL:", API_URL);

    fetchDashboardData();
  }, []);

  useEffect(() => {
    // Create WebSocket connection for real-time stock updates
    const wsUrl = API_URL.replace('http://', 'ws://').replace('https://', 'wss://');
    const ws = new WebSocket(`${wsUrl}/ws`);

    ws.onmessage = (event) => {
      try {
        const data = JSON.parse(event.data);

        if (data.type === 'new_crossing_stocks' && data.data.length > 0) {
          // Update the recently crossed stocks
          setRecentlyCrossedStocks(prevStocks => {
            // Add new stocks to the beginning of the array
            const newStocks = [...data.data, ...prevStocks];
            // Keep only last 10 stocks to avoid cluttering the UI
            return newStocks.slice(0, 10);
          });
        }
      } catch (error) {
        console.error('Error processing WebSocket message:', error);
      }
    };

    // Clean up on component unmount
    return () => {
      if (ws) {
        ws.close();
      }
    };
  }, []);

  const fetchDashboardData = async () => {
    setLoading(true);
    setError('');

    try {
      const [gainersRes, losersRes, activeRes, crossingRes] = await Promise.all([
        axios.get(`${API_URL}/api/stocks/gainers`),
        axios.get(`${API_URL}/api/stocks/losers`),
        axios.get(`${API_URL}/api/stocks/most-active`),
        axios.get(`${API_URL}/api/stocks/screener/crossing-prev-day-high`)
      ]);

      setTopGainers(gainersRes.data.stocks || []);
      setTopLosers(losersRes.data.stocks || []);
      setMostActive(activeRes.data.stocks || []);
      setCrossingStocks(crossingRes.data.stocks || []);

      setLastUpdated(new Date());
      setUsingDemoData(
        gainersRes.data.is_demo || 
        losersRes.data.is_demo || 
        activeRes.data.is_demo
      );

    } catch (err) {
      console.error("Error fetching dashboard data:", err);
      setError('Error fetching market data. Please try again later.');
    } finally {
      setLoading(false);
    }
  };

  const refreshData = () => {
    fetchDashboardData();
  };

  // Generate sample price history data for chart
  const generateChartData = () => {
    const data = [];
    const now = new Date();
    for (let i = 30; i >= 0; i--) {
      const date = new Date(now);
      date.setDate(date.getDate() - i);
      data.push({
        date: date.toLocaleDateString('en-US', { month: 'short', day: 'numeric' }),
        'S&P 500': 4400 + Math.random() * 200,
        'NASDAQ': 15800 + Math.random() * 700,
      });
    }
    return data;
  };

  const chartData = generateChartData();

  const handleTabChange = (event, newValue) => {
    setActiveTab(newValue);

    // Update URL based on selected tab
    if (newValue === 'gainers') {
      navigate('/?filter=gainers');
    } else if (newValue === 'losers') {
      navigate('/?filter=losers');
    } else if (newValue === 'most-active') {
      navigate('/?filter=mostActive');
    } else {
      navigate('/');
    }
  };

  const renderStockList = (stocks, loading, error) => {
    if (loading) {
      return (
        <Box sx={{ display: 'flex', justifyContent: 'center', p: 2 }}>
          <CircularProgress size={24} />
        </Box>
      );
    }

    if (error) {
      return (
        <Box sx={{ p: 2, color: 'error.main' }}>
          <Typography>{error}</Typography>
        </Box>
      );
    }

    if (!stocks || stocks.length === 0) {
      return (
        <Box sx={{ p: 2 }}>
          <Typography>No stocks available</Typography>
        </Box>
      );
    }

    return (
      <List disablePadding>
        {stocks.map((stock, index) => (
          <ListItem 
            key={index} 
            button 
            component={Link} 
            to={`/stock/${stock.symbol}`}
            sx={{ 
              borderBottom: '1px solid rgba(0, 0, 0, 0.08)',
              py: 1
            }}
          >
            <ListItemText 
              primary={stock.symbol} 
              secondary={stock.name || 'Unknown'} 
              primaryTypographyProps={{ fontWeight: 'bold' }}
            />
            <Box sx={{ display: 'flex', flexDirection: 'column', alignItems: 'flex-end' }}>
              <Typography variant="body2" sx={{ fontWeight: 'medium' }}>
                ${parseFloat(stock.price || 0).toFixed(2)}
              </Typography>
              <Typography 
                variant="body2" 
                color={parseFloat(stock.percent_change || 0) >= 0 ? 'success.main' : 'error.main'}
              >
                {parseFloat(stock.percent_change || 0) >= 0 ? '+' : ''}
                {parseFloat(stock.percent_change || 0).toFixed(2)}%
              </Typography>
              <Typography variant="caption" color="text.secondary">
                Vol: {stock.volume || 'N/A'}
              </Typography>
            </Box>
          </ListItem>
        ))}
      </List>
    );
  };

  const renderRecentlyCrossedStocks = () => {
    if (recentlyCrossedStocks.length === 0) {
      return (
        <Alert severity="info" sx={{ mt: 2 }}>
          No stocks have crossed above their previous day high recently. Notifications will appear here when new crosses happen.
        </Alert>
      );
    }

    return (
      <Box sx={{ mt: 2 }}>
        {recentlyCrossedStocks.map((stock, index) => (
          <Box key={`${stock.symbol}-${index}`} sx={{ mb: 2 }}>
            <StockCrossingAlert stock={stock} />
          </Box>
        ))}
      </Box>
    );
  };

  return (
    <Container maxWidth="lg" sx={{ py: 4 }}>
      <Grid container spacing={3}>
        <Grid item xs={12}>
          <Box sx={{ borderBottom: 1, borderColor: 'divider', mb: 3 }}>
            <Tabs 
              value={activeTab} 
              onChange={handleTabChange} 
              aria-label="market trend tabs"
              variant="scrollable"
              scrollButtons="auto"
            >
              <Tab 
                icon={<TrendingUpIcon fontSize="small" />} 
                iconPosition="start" 
                label="Popular" 
                value="popular" 
              />
              <Tab 
                icon={<ArrowUpwardIcon fontSize="small" />} 
                iconPosition="start" 
                label="Top Gainers" 
                value="gainers" 
              />
              <Tab 
                icon={<ArrowDownwardIcon fontSize="small" />} 
                iconPosition="start" 
                label="Top Losers" 
                value="losers" 
              />
              <Tab 
                icon={<ShowChartIcon fontSize="small" />} 
                iconPosition="start" 
                label="Most Active" 
                value="most-active" 
              />
            </Tabs>
          </Box>
        </Grid>

        <Grid item xs={12}>
          {activeTab === 'popular' && (
            <Paper elevation={1} sx={{ p: 0 }}>
              <Box sx={{ p: 2, borderBottom: '1px solid rgba(0, 0, 0, 0.12)', display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                <Typography variant="h6" fontWeight="medium">Popular Stocks</Typography>
                <Chip 
                  label={`${popularStocks.length} stocks`} 
                  size="small" 
                  color="info" 
                  sx={{ fontWeight: 500 }}
                />
              </Box>
              {renderStockList(popularStocks, loading, error)}
            </Paper>
          )}

          {activeTab === 'gainers' && (
            <Paper elevation={1} sx={{ p: 0 }}>
              <Box sx={{ p: 2, borderBottom: '1px solid rgba(0, 0, 0, 0.12)', display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                <Typography variant="h6" fontWeight="medium">Top Gainers</Typography>
                <Chip 
                  label={`${topGainers.length} stocks`} 
                  size="small" 
                  color="success" 
                  sx={{ fontWeight: 500 }}
                />
              </Box>
              {renderStockList(topGainers, loading, error)}
            </Paper>
          )}

          {activeTab === 'losers' && (
            <Paper elevation={1} sx={{ p: 0 }}>
              <Box sx={{ p: 2, borderBottom: '1px solid rgba(0, 0, 0, 0.12)', display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                <Typography variant="h6" fontWeight="medium">Top Losers</Typography>
                <Chip 
                  label={`${topLosers.length} stocks`} 
                  size="small" 
                  color="error" 
                  sx={{ fontWeight: 500 }}
                />
              </Box>
              {renderStockList(topLosers, loading, error)}
            </Paper>
          )}

          {activeTab === 'most-active' && (
            <Paper elevation={1} sx={{ p: 0 }}>
              <Box sx={{ p: 2, borderBottom: '1px solid rgba(0, 0, 0, 0.12)', display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                <Typography variant="h6" fontWeight="medium">Most Active</Typography>
                <Chip 
                  label={`${mostActive.length} stocks`} 
                  size="small" 
                  color="primary" 
                  sx={{ fontWeight: 500 }}
                />
              </Box>
              {renderStockList(mostActive, loading, error)}
            </Paper>
          )}
        </Grid>

        <Grid item xs={12}>
          <Paper elevation={2} sx={{ p: 2 }}>
            <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 2 }}>
              <Typography variant="h5" component="h2">
                <TrendingUpIcon sx={{ verticalAlign: 'middle', mr: 1 }} />
                Recently Crossed Above Previous Day High
              </Typography>
              <Chip
                color="success"
                label="Real-time Updates"
                variant="outlined"
                size="small"
              />
            </Box>
            <Divider sx={{ mb: 2 }} />
            {renderRecentlyCrossedStocks()}
          </Paper>
        </Grid>
      </Grid>
    </Container>
  );
};

export default Dashboard;
