  // Format volume for display - handle undefined values
  const formatVolume = (value) => {
    // Return a default value for truly empty values
    if (value === undefined || value === null) {
      return '0';
    }

    // Handle string values that may contain 'M' or 'K' already
    if (typeof value === 'string') {
      // Handle empty strings
      if (value.trim() === '') {
        return '0';
      }

      // If it's already formatted (like "59.0M"), just return it
      if (value.endsWith('M') || value.endsWith('K') || value.endsWith('B') || 
          value.includes('million') || value.includes('thousand') || value.includes('billion')) {
        return value;
      }

      // Otherwise convert the string to a number, removing commas
      value = Number(String(value).replace(/,/g, ''));
    }

    // Ensure value is a number
    const numValue = Number(value);

    if (isNaN(numValue)) {
      return '0';
    }

    // Format based on magnitude
    if (numValue >= 1000000) {
      return `${(numValue / 1000000).toFixed(1)}M`;
    } else if (numValue >= 1000) {
      return `${(numValue / 1000).toFixed(0)}K`;
    }
    return numValue.toString();
  };

  // Format percentage change values for display
  const formatPercentageChange = (value) => {
    if (value === undefined || value === null || value === '') {
      return 'N/A';
    }

    // If the value already contains a % sign, it's pre-formatted from the backend
    if (typeof value === 'string' && value.includes('%')) {
      return value;
    }

    // Convert string values to numbers
    let numValue;
    if (typeof value === 'string') {
      numValue = parseFloat(value);
    } else {
      numValue = value;
    }

    if (isNaN(numValue)) {
      return 'N/A';
    }

    return `${numValue >= 0 ? '+' : ''}${numValue.toFixed(2)}%`;
  };

  // Improve the formatPrice function to handle more formats
  const formatPrice = (value) => {
    if (value === undefined || value === null || value === '') {
      return 'N/A';
    }

    // If the value already contains a $ sign, it's pre-formatted from the backend
    if (typeof value === 'string' && value.includes('$')) {
      return value;
    }

    // Convert string values to numbers, handling "$" prefix
    let numValue;
    if (typeof value === 'string') {
      // Remove "$" if present
      numValue = parseFloat(value.replace('$', ''));
    } else {
      numValue = value;
    }

    if (isNaN(numValue)) {
      return 'N/A';
    }

    return `$${numValue.toFixed(2)}`;
